mapper.select:
SELECT f_com_code_to_nm(FG) AS FG, VEND_ID AS VEND_ID, VEND_NAME AS VEND_NAME, BIZNO AS BIZNO, TELNO AS TELNO, REMK AS REMK
FROM VEND
AND VEND_ID = #{vendId}

mapper.update:
UPDATE VEND
SET VEND_NAME = #{vendName}, BIZNO = #{bizno}, TELNO = #{telno}, REMK = #{remk}, FG = f_com_nm_to_code(#{fg})
WHERE VEND_ID = #{vendId}
