mapper.select:
SELECT E.EQM_ID AS EQM_ID, E.EQM_NAME AS EQM_NAME, P.PRCS_ID AS PRCS_ID, P.NM AS NM, E.USE_YN AS USE_YN, E.FG AS FG, E.TEMP_MAX AS TEMP_MAX, E.TEMP_MIN AS TEMP_MIN, E.CHCK_PERD AS CHCK_PERD, E.EQM_IMG AS EQM_IMG FROM EQM E LEFT OUTER JOIN PRCS P ON (E.EQM_ID = P.EQM_ID)
E.FG = F_COM_NM_TO_CODE(#{gubun})
AND USE_YN = #{useYnCk}
