mapper.select:
SELECT E.EQM_ID AS EQM_ID, E.EQM_NAME AS EQM_NAME, E.CHCK_PERD ||'일' AS CHCK_PERD, F_COM_CODE_TO_NM(E.FG) AS FG, C.CHCK_DT AS CHCK_DT, C.JUDT AS JUDT, C.CHCK_DETA AS CHCK_DETA, (C.CHCK_DT + E.CHCK_PERD) AS NM_CK_DT, C.INSPR FROM EQM E LEFT OUTER JOIN CHCK C ON (E.EQM_ID = C.EQM_ID)
E.EQM_ID = #{eqmId}
TO_CHAR(CHCK_DT, 'YYYY-MM-DD') = #{chckDt}
AND E.FG = F_COM_NM_TO_CODE(#{gubun})
AND C.CHCK_DT IS NOT NULL
