mapper.select:
SELECT A.*, E.EQM_NAME FROM ( SELECT O.*, R.RESN_NAME FROM EQM_NON_OPR O, EQM_NON_OPR_RESN R WHERE O.RESN_ID = R.RESN_ID
AND O.EQM_ID = #{eqmId}
) A, EQM E
WHERE A.EQM_ID = E.EQM_ID
AND TRUNC(NON_OPR_FR_TM) >= TO_CHAR(#{fromDate})
AND TRUNC(NON_OPR_TO_TM) <= TO_CHAR(#{toDate})
AND A.EQM_ID = #{eqmId}

mapper.update:
UPDATE  EQM
SET 	USE_YN = 'N'
WHERE   EQM_ID = #{eqmId}
