mapper.select:
select pid.indica_deta_id as indica_deta_id, p.prdt_nm as prdt_nm, ppd.prdt_id as prdt_id, pid.qy as qy, pid.line_id as line_id, pid.ord as ord from prod_indica_deta pid join prod_indica pi on pid.indica_id = pi.indica_id join prod_plan_deta ppd on pi.plan_deta_id = ppd.plan_deta_id join prdt p on ppd.prdt_id = p.prdt_id where pid.indica_deta_id = #{indicaDetaId}
UNION
select pid.indica_deta_id as indica_deta_id, r.nm as prdt_nm, ppd.prdt_id as prdt_id, pid.qy as qy, pid.line_id as line_id, pid.ord as ord from prod_indica_deta pid join prod_indica pi on pid.indica_id = pi.indica_id join prod_plan_deta ppd on pi.plan_deta_id = ppd.plan_deta_id join rwmatr r on ppd.prdt_id = r.rwmatr_id where pid.indica_deta_id = #{indicaDetaId}

select pn_id(#{indicaDetaId}) as prcs_now_id, l.prcs_id as prcs_id, p.nm as nm, p.eqm_id as eqm_id, e.eqm_name as eqm_name, e.mngr as mngr, l.ord as ord from line l join prcs p on l.prcs_id = p.prcs_id join eqm e on p.eqm_id = e.eqm_id join prdt pr on l.prdt_id = pr.prdt_id where pr.prdt_nm = #{prdtNm}
union
select pn_id(#{indicaDetaId}) as prcs_now_id, l.prcs_id as prcs_id, p.nm as nm, p.eqm_id as eqm_id, e.eqm_name as eqm_name, e.mngr as mngr, l.ord as ord from line l join prcs p on l.prcs_id = p.prcs_id join eqm e on p.eqm_id = e.eqm_id join rwmatr r on l.prdt_id = r.rwmatr_id where r.nm = #{prdtNm}
order by ord
